// Register.js
import React, { useState } from 'react';
import axios from 'axios';
import './Register.css'; // Import a CSS file for styling

const Register = () => {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: ''
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:5000/api/auth/register', formData)
            .then(response => {
                alert(response.data.message);
            })
            .catch(error => {
                alert('Error: ' + error.response.data.error);
            });
    };

    return (
        <form onSubmit={handleSubmit} className="register-form">
            <div className="form-group">
                <label htmlFor="email"><i className="fas fa-envelope"></i> Email</label>
                <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="Email"
                    required
                />
            </div>

            <div className="form-group">
                <label htmlFor="username"><i className="fas fa-user"></i> Username</label>
                <input
                    type="text"
                    name="username"
                    value={formData.username}
                    onChange={handleChange}
                    placeholder="Username"
                    required
                />
            </div>

            <div className="form-group">
                <label htmlFor="password"><i className="fas fa-key"></i> Password</label>
                <input
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    placeholder="Password"
                    required
                />
            </div>

            <button type="submit" className="register-btn"><i className="fas fa-user-plus"></i> Register</button>
            
            <p className="login-link">Sudah punya akun silahkan <a href="/login">Login Disini!</a></p>
        </form>
    );
};

export default Register;
